/* Grid set up here (see below) so it is at the top of the CSS stylesheet */

/* Import font files */

@import url("../../fonts/addington-cf/addington-cf-demi-bold.css");
@import url("../../fonts/metro-sans/metro-sans-regular/metro-sans-regular.css");
@import url("../../fonts/metro-sans/metro-sans-regular-italic/metro-sans-regular-italic.css");
@import url("../../fonts/metro-sans/metro-sans-bold/metro-sans-bold.css");
@import url("../../fonts/metro-sans/metro-sans-bold-italic/metro-sans-bold-italic.css");


/* Grid set up */

body.site {
	display: grid;
	grid-template-areas:
		"header"
		"content"
		"footer";
	transition:[slide-navigation-transition];
	overflow-x: hidden;
}

header#masthead,
.header-background {
	grid-area: header;
}

main#main {
	grid-area: content;
	max-width: 100vw;
}

footer#footer {
	grid-area: footer;
}

#website-navigation {
	position: relative;
	width:[slide-navigation-width];
	max-width: 100vw;
	left: -[slide-navigation-width];
	grid-column: 1;
	grid-row: 1/-1;
	z-index: 10000;
	background: [colour-nav-dropdown-background];
	transition:[slide-navigation-transition];
}

.reveal-menu #website-navigation {
	left: 0;
}

#website-navigation ul {
	position: fixed;
	display: none;
	flex-direction: column;
	width: [slide-navigation-width];
}

#website-navigation.open ul {
	display: flex;
}

