/* Screen Readers only */

.sr-only { /* From https://www.linkedin.com/learning/simplifying-web-development-with-accessibility-best-practices/how-to-hide-visual-content?autoSkip=true&autoplay=true&resume=false&u=92735081 */
	position: absolute;
	clip: rect(1px, 1px, 1px, 1px);
	clip-path: inset(50%);
	width: 1px;
	height: 1px;
	margin: -1px;
	overflow: hidden;
	padding: 0;
}


/* Other elements */

section.kokako .col-xs-12 > :is(h1, h2):first-child {
	margin-top: 0;
}

ul {
	list-style-type: disc;
}

