	content: "\200B";  /* Unicode zero width space */
	white-space: normal;
}


/* List items */

ul.no-bullets li {
	list-style-type: none;
	margin: 0 0 10px 0;
}

:is(ul.tick, ul.ticks, section.kokako.ul-ticks ul) li {
	list-style-type: none;
	position: relative;
	margin: 0 0 10px 35px;
}

:is(ul.tick, ul.ticks, section.kokako.ul-ticks ul) li:before {
