/* New hover - adapted from https://codepen.io/Kseso/pen/bfzjC */

*:after, *:before {
	box-sizing: border-box;
	-moz-box-sizing: border-box;
}

a[anchor-underline-exclusions] {
	position: relative;
	outline: none;
	text-decoration: none;
	z-index: 1;
}

a[anchor-underline-exclusions]:before {
	content: "";
	position: absolute;
	width: 0;
	height: 3px;
	bottom: -3px;
	left: 50%;
	background: [colour-anchor-underline];
	visibility: hidden;
	-webkit-transform: translate3d(0,0,0); /* Fix for Google Chrome flickers on subsequent anchor elements - see https://maximelafarie.com/avoid-css-flickering */
	transition: [anchor-transition];
}

a[anchor-underline-exclusions]:hover:before {
	visibility: visible;
	-webkit-transform: translate3d(0,0,0);
	width: 100%;
	left: 0;
	transition: [anchor-transition];
}


