@keyframes slideUpScale {
	from {
		transform: translateY([animation-start-translate-y]) scale([animation-start-scale]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: translateY(0) scale(1);
		opacity: 1;
	}
}

@keyframes slideRightScale {
	from {
		transform: translateX(-[animation-start-translate-x]) scale([animation-start-scale]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: translateX(0) scale(1);
		opacity: 1;
	}
}

@keyframes slideDownScale {
	from {
		transform: translateY(-[animation-start-translate-y]) scale([animation-start-scale]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: translateY(0) scale(1);
		opacity: 1;
	}
}


@keyframes slideDownBounceScale {
	0% {
		transform: scale([animation-start-scale]);
		opacity: [animation-start-opacity];
	}
	70% {
		transform: scale(1.02);
		opacity: 1;
	}
	100% {
		transform: scale(1);
		opacity: 1;
	}
}

@keyframes slideLeftScale {
	from {
		transform: translateX([animation-start-translate-x]) scale([animation-start-scale]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: translateX(0) scale(1);
		opacity: 1;
	}
}

@keyframes slideUp {
	from {
		transform: translateY([animation-start-translate-y]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: translateY(0);
		opacity: 1;
	}
}

@keyframes slideRight {
	from {
		transform: translateX(-[animation-start-translate-x]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: translateX(0);
		opacity: 1;
	}
}

@keyframes slideDown {
	from {
		transform: translateY(-[animation-start-translate-y]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: translateY(0);
		opacity: 1;
	}
}

@keyframes slideLeft {
	from {
		transform: translateX([animation-start-translate-x]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: translateX(0);
		opacity: 1;
	}
}

@keyframes scale {
	from {
		transform: scale([animation-start-scale]);
		opacity: [animation-start-opacity];
	}
	to {
		transform: scale(1);
		opacity: 1;
	}
}

@keyframes clear {
	to {
		transform: none;
		opacity: 1;
	}
}

