/* Footer */

footer {
    padding-bottom: 200px;
}

header {
	background: [colour-header-background]; /* Fall back */
	background: [colour-header-background-rgba {header-mobile-opacity}]
}

/* .header-background {
	display: none !important;
} */

header .navbar-collapse {
	position: absolute;
	width: calc(100% + 60px);
	top: [header-height-scrolled];
	left: -30px;
	align-items: normal; /* This is important for a smooth transition when menu collapsed! */
	transition: [header-transition];
	margin-top: 0;
	transition: [header-transition];
}

header.scrolled .navbar-collapse,
header .navbar-collapse.show {
	transition: [header-transition];
}

header .navbar-collapse.collapse {
	height: 0;
	transition: [header-transition];
}

header .navbar-collapse.collapse.show {
	height: auto;
	transition: [header-transition];
}

header .navbar .logo {
	z-index: 9999;
}

header nav.navbar #main-nav {
	background: [colour-nav-dropdown-background]; /* Fall back */
	background: [colour-nav-dropdown-background-rgba {nav-dropdown-menu-opacity}];
	width: 100%;
	z-index: 1000;
	margin-bottom: 0;
}

header.scrolled .logo a.navbar-brand {
	margin-top: 0;
}

header nav button:focus {
	outline-color: [colour-brand-tan];
}

nav .collapse:not(.show) {
	display: none !important; /* Required to override the !important statement on the .d-flex class - otherwise menu always shows */
}

header nav button.navbar-toggler {
	position: relative;
	top: 0;
	border-radius: 0;
	transition: [header-transition];
	z-index: 1000;
}

header nav ul li:not(:last-child):after {
	content: "";
}

header #main-nav ul li {
	margin: 0;
}

header #main-nav ul li a {
	display: block;
	width: 100%;
	margin: 0;
	text-align: center;
}

header #main-nav ul li :is(a, a:link, a:visited) {
	color: [colour-nav-dropdown-text];
	background: transparent;
}

header #main-nav ul li a:hover {
	color: [colour-nav-dropdown-text];
	background: [colour-nav-dropdown-background-hover]; /* Fall back */
	background: [colour-nav-dropdown-background-rgba {nav-dropdown-menu-opacity}];
}

header #main-nav ul li a:active {
	color: [colour-nav-dropdown-text];
	background: [colour-nav-dropdown-background-active];
}

header .navbar-collapse {
	z-index: 100;
}

.navbar-nav .dropdown-menu {
	text-align: center;
	transform: inherit;
	background: transparent;
}

.navbar-nav .dropdown-menu .dropdown-menu {
	width: 100%;
}

nav a.nav-link:before, nav a.dropdown-item:before {
	background: transparent;
}

header #main-nav ul li a:is(.dropdown-item, .dropdown-item:link, .dropdown-item:visited) {
	color: [colour-nav-dropdown-text-rgba 0.7];
}

header #main-nav ul li a.dropdown-item:hover {
	color: [colour-nav-dropdown-text-rgba 0.6];
}

header #main-nav ul li a.dropdown-item:active {
	color: [colour-nav-dropdown-text-rgba 0.55];
}


/* Admin bar positioning */

body.header-fixed.logged-in.admin-bar header { /* Not required when header is positioned relative */
	top: 0;
}
